def is_prime(x):
    prime = True
    y = int(x**0.5)
    if(x ==1):
        return False
    if(x != 2):
        if(x%2 == 0):
            return False
    for i in range(3, y+1):
        if(x%i == 0):
            return False
        i += 1
    return prime

